const content = extractText($json);
if (!content) throw new Error("Missing ScriptAgent content");

let scriptPack;
try {
  scriptPack = JSON.parse(content);
} catch (e) {
  throw new Error("ScriptAgent returned invalid JSON: " + content);
}

// basic validation so you don't pass junk downstream
if (!scriptPack.title || !scriptPack.narration) {
  throw new Error("scriptPack.title or scriptPack.narration missing/empty");
}

return [{
  json: {
    ...$json,
    scriptPack
  }
}];

const compliance = $json.compliance;
const scriptPack = $json.scriptPack;

if (!compliance) throw new Error("Missing compliance on input");
if (!scriptPack) throw new Error("Missing scriptPack on input");

// Validate compliance structure
if (typeof compliance.pass !== "boolean") {
  throw new Error(`compliance.pass must be boolean, got: ${typeof compliance.pass}`);
}
if (!Array.isArray(compliance.issues)) {
  compliance.issues = [];
}
if (!Array.isArray(compliance.fixes)) {
  compliance.fixes = [];
}

const fixed = { ...scriptPack };

// Ensure affiliateDisclosure exists
if (typeof fixed.affiliateDisclosure !== "string") fixed.affiliateDisclosure = "";

// Apply field-based fixes from ComplianceAgent
for (const f of (compliance.fixes || [])) {
  if (!f?.field || typeof f.with !== "string") continue;

  if (f.field === "disclaimer") {
    fixed.disclaimer = [fixed.disclaimer || "", f.with].filter(Boolean).join(" ").trim();
  } else if (f.field === "affiliateDisclosure") {
    fixed.affiliateDisclosure = f.with.trim();
  } else if (f.field === "narration") {
    // Minimal safe approach: append (don‚Äôt overwrite your whole narration)
    fixed.narration = [fixed.narration || "", f.with].filter(Boolean).join(" ").trim();
  }
}

// Make sure requiredDisclaimers are present in disclaimer
const req = Array.isArray(compliance.requiredDisclaimers) ? compliance.requiredDisclaimers : [];
for (const d of req) {
  if (typeof d === "string" && d.trim() && !String(fixed.disclaimer || "").includes(d.trim())) {
    fixed.disclaimer = [fixed.disclaimer || "", d.trim()].filter(Boolean).join(" ").trim();
  }
}

// Make sure affiliate disclosure present if compliance wants it
if (compliance.affiliateDisclosure && !fixed.affiliateDisclosure.trim()) {
  fixed.affiliateDisclosure = String(compliance.affiliateDisclosure).trim();
}

// After applying all fixes, update pass status to true since fixes have been applied
const updatedCompliance = {
  ...compliance,
  pass: true  // Fixes have been applied, so mark as passing
};

return [{
  json: {
    ...$json,
    scriptPack: fixed,
    compliance: updatedCompliance
  }
}];

const outline = $json.outline;
const scriptPack = $json.scriptPack;

if (!outline) throw new Error("Missing outline");
if (!scriptPack) throw new Error("Missing scriptPack");

const scenes = (outline.sections || []).map((s, idx) => ({
  type: "panel",
  headline: s.title || `Section ${idx + 1}`,
  bullets: Array.isArray(s.bullets) ? s.bullets : [],
  durationSeconds: 10
}));

const props = {
  video: {
    title: outline.title || scriptPack.title || "Automated Realty Playbook",
    subtitle: "Luxury-tech AI real estate automation",
    theme: "hybrid-luxury-tech"
  },
  scenes,
  audio: {
    voiceoverKey: `${$json.r2Prefix}/audio/vo.mp3`,
    narration: scriptPack.narration
  }
};

// Store as text so MoveBinaryData can convert to propsbin
return [{ json: { ...$json, propsText: JSON.stringify(props) } }];

