const outline = $json.outline;
const strategy = $json.strategy;
const scriptPack = $json.scriptPack;

const titleBase = outline?.title || strategy?.topic || "Automated Realty Playbook";
const youtubeTitle = titleBase.length > 95 ? titleBase.slice(0, 95) : titleBase;

const affiliateDisclosure =
  "Disclosure: Some links may be affiliate links. I may earn a commission at no extra cost to you.";

const youtubeDescription = [
  (outline?.hook ? outline.hook + "\n" : ""),
  "In this video, we break down practical ways to use AI to automate your real estate workflow‚Äîlead follow-up, listings, content, and deal analysis.",
  "",
  "‚úÖ Subscribe for more AI + real estate automation.",
  "",
  affiliateDisclosure,
  "",
  (scriptPack?.disclaimer || "Not legal, tax, or financial advice. Consult a professional.")
].join("\n");

const youtubeTags = [
  "ai real estate",
  "real estate automation",
  "chatgpt real estate",
  "real estate ai tools",
  "automated realty playbook"
];

return [{
  json: {
    ...$json,
    youtubeTitle,
    youtubeDescription,
    youtubeTags,
    hashtags: ["#AI", "#RealEstate", "#Automation"],
    pinnedComment: "Want the free AI toolkit? Comment 'TOOLKIT' and I‚Äôll send it.",
    affiliateDisclosure,
    privacyStatus: "private",
    categoryId: "26"
  }
}];

const manifest = {
  runId: $json.runId,
  r2Bucket: $json.r2Bucket,
  r2Prefix: $json.r2Prefix,
  videoUrl: $json.videoUrl,
  logUrl: $json.logUrl,
  youtube: {
    title: $json.youtubeTitle,
    description: $json.youtubeDescription,
    tags: $json.youtubeTags,
    privacyStatus: $json.privacyStatus,
    categoryId: $json.categoryId
  }
};
return [{...$json, manifestText: JSON.stringify(manifest)}];

const body = $json.body || {};
let payloadStr = body.payload;
if (!payloadStr && typeof body === 'string' && body.startsWith('payload=')) payloadStr = decodeURIComponent(body.slice('payload='.length));
if (!payloadStr) throw new Error("Missing Slack interactivity payload");
const payload = JSON.parse(payloadStr);
const raw = payload.actions?.[0]?.value || "";
const [status, runId] = raw.split("|");
if (!status || !runId) throw new Error("Missing status/runId in Slack button value");
return [{status, runId, payload}];

const m = $json;
const manifest = m.json || m; // depending on node output
const data = manifest;
return [{
  runId: data.runId,
  videoUrl: data.videoUrl,
  logUrl: data.logUrl,
  youtubeTitle: data.youtube.title,
  youtubeDescription: data.youtube.description,
  youtubeTags: data.youtube.tags,
  privacyStatus: data.youtube.privacyStatus,
  categoryId: data.youtube.categoryId
}];

