function extractText(j) {
  return (
    j?.output?.[0]?.content?.[0]?.text ??          // OpenAI Chat node (common)
    j?.output_text ??                              // some variants
    j?.message?.content ??                         // some n8n variants
    j?.choices?.[0]?.message?.content ??           // legacy chat.completions
    j?.body?.choices?.[0]?.message?.content ??     // http request sometimes wraps in body
    j?.data?.choices?.[0]?.message?.content ??     // other wrappers
    null
  );
}

const content = extractText($json);
if (!content) {
  throw new Error("Missing ComplianceAgent content (no text found in known fields)");
}

let compliance;
try {
  compliance = typeof content === "string" ? JSON.parse(content) : content;
} catch (e) {
  throw new Error("ComplianceAgent returned invalid JSON: " + content);
}

// Validate and normalize the pass field
if (typeof compliance.pass !== "boolean") {
  // Attempt to coerce common string values
  if (compliance.pass === "true") {
    compliance.pass = true;
  } else if (compliance.pass === "false") {
    compliance.pass = false;
  } else {
    throw new Error(`compliance.pass must be boolean, got: ${typeof compliance.pass} = ${compliance.pass}`);
  }
}

// Log for debugging (remove after testing)
console.log("Compliance pass value:", compliance.pass);
console.log("Compliance pass type:", typeof compliance.pass);
console.log("Full compliance object:", JSON.stringify(compliance, null, 2));

// IMPORTANT: always return { json: { ... } }
return [{
  json: {
    ...$json,
    compliance
  }
}];

// Validate required fields before Render Long
if (!$json.runId) {
  throw new Error("Missing runId for render request");
}
if (!$json.r2Prefix) {
  throw new Error("Missing r2Prefix for render request");
}

console.log("Validation passed for Render Long. runId:", $json.runId);
return [$json];

// Validate videoUrl before download
if (!$json.videoUrl) {
  throw new Error("Missing videoUrl from manifest");
}

// Check if URL is valid
const urlPattern = /^https?:\/\/.+/;
if (!urlPattern.test($json.videoUrl)) {
  throw new Error(`Invalid videoUrl format: ${$json.videoUrl}`);
}

console.log("Validation passed for video download. URL:", $json.videoUrl);
return [$json];
